    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
        double p , t;
        
        
        p = Double.parseDouble(pago.getText());
        t = Double.parseDouble(total.getText());
        
        respuesta.setText(String.valueOf(multiplicacion(p, t)));
       
        
    }//GEN-LAST:event_jButton1MouseClicked

    private void sumaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_sumaMouseClicked
        double n1,n2,n3,n4,n5,n6;
        
        n1 = Double.parseDouble(d1.getText());
        n2 = Double.parseDouble(d2.getText());
        n3 = Double.parseDouble(d3.getText());
        n4 = Double.parseDouble(d4.getText());
        n5 = Double.parseDouble(d5.getText());
        n6 = Double.parseDouble(d6.getText());
        
        total.setText(String.valueOf(suma(n1,n2,n3,n4,n5,n6)));
        
    }//GEN-LAST:event_sumaMouseClicked

